import unittest
from selenium import webdriver
import page
from selenium.webdriver.chrome.options import Options


class TestGoogle(unittest.TestCase):

    def setUp(self):
        self.chrome_options = Options()
        self.driver = webdriver.Chrome('webdrivers/chromedriver', options=self.chrome_options)
        self.driver.get("https://www.google.co.nz")

    def test_key_word_search(self):
        content = "animal"
        main_page = page.HomePage(self.driver)
        assert main_page.is_title_matches(), "Home page load failed!"
        main_page.input_search_content(content)
        main_page.click_search_button()
        search_result_page = page.SearchResultsPage(self.driver)
        assert search_result_page.check_search_result(content), "{} search failed".format(content)

    def test_voice_input_search(self):
        SomeVoiceLib = "initial"
        main_page = page.HomePage(self.driver)
        assert main_page.is_title_matches(), "Home page load failed!"
        main_page.voice_input_content(SomeVoiceLib)

        search_result_page = page.SearchResultsPage(self.driver)
        assert search_result_page.check_search_result(SomeVoiceLib), "{} voice search failed".format(SomeVoiceLib)

    def test_image_input_search(self):
        main_page = page.HomePage(self.driver)
        assert main_page.is_title_matches(), "Home page load failed!"
        imageLink = "data:image/jpeg;base64,"

        main_page.click_image_button()
        main_page.image_url_input(imageLink)
        main_page.image_search_click()

        search_result_page = page.SearchResultsPage(self.driver)
        search_result_page.check_image_search_result()

        assert search_result_page.check_image_search_result(), "{} image search failed!".format(imageLink)

    def tearDown(self):
        self.driver.close()
